'use client'

import React, { forwardRef, useRef } from 'react'
import { ny } from '~/lib/utils'
import { AnimatedBeam } from '~/components/ui/animated-beam'

const Circle = forwardRef<
   HTMLDivElement,
   { className?: string, children?: React.ReactNode }
>(({ className, children }, ref) => {
   return (
      <div
         ref={ref}
         className={ny(
            ' z-10 flex size-12 items-center justify-center rounded-full border-2 bg-white p-3 shadow-[0_0_20px_-12px_rgba(0,0,0,0.8)]',
            className,
         )}
      >
         {children}
      </div>
   )
})

export function AnimatedBeamMultipleInputDemo() {
   const containerRef = useRef<HTMLDivElement>(null)
   const div1Ref = useRef<HTMLDivElement>(null)
   const div2Ref = useRef<HTMLDivElement>(null)
   const div3Ref = useRef<HTMLDivElement>(null)
   const div4Ref = useRef<HTMLDivElement>(null)
   const div5Ref = useRef<HTMLDivElement>(null)
   const div6Ref = useRef<HTMLDivElement>(null)
   const div7Ref = useRef<HTMLDivElement>(null)

   return (
      <div
         className="relative flex size-full max-w-lg items-center justify-center overflow-hidden rounded-lg  p-10 md:shadow-xl"
         ref={containerRef}
      >
         <div className="flex size-full flex-row items-stretch justify-between gap-10">
            <div className="flex flex-col justify-center gap-2">
               <Circle ref={div1Ref}>
                  <Icons.googleDrive />
               </Circle>
               <Circle ref={div2Ref}>
                  <Icons.googleDocs />
               </Circle>
               <Circle ref={div3Ref}>
                  <Icons.whatsapp />
               </Circle>
               <Circle ref={div4Ref}>
                  <Icons.messenger />
               </Circle>
               <Circle ref={div5Ref}>
                  <Icons.notion />
               </Circle>
            </div>
            <div className="flex flex-col justify-center">
               <Circle ref={div6Ref} className="size-16">
                  <Icons.openai />
               </Circle>
            </div>
            <div className="flex flex-col justify-center">
               <Circle ref={div7Ref}>
                  <Icons.user />
               </Circle>
            </div>
         </div>

         <AnimatedBeam
            containerRef={containerRef}
            fromRef={div1Ref}
            toRef={div6Ref}
         />
         <AnimatedBeam
            containerRef={containerRef}
            fromRef={div2Ref}
            toRef={div6Ref}
         />
         <AnimatedBeam
            containerRef={containerRef}
            fromRef={div3Ref}
            toRef={div6Ref}
         />
         <AnimatedBeam
            containerRef={containerRef}
            fromRef={div4Ref}
            toRef={div6Ref}
         />
         <AnimatedBeam
            containerRef={containerRef}
            fromRef={div5Ref}
            toRef={div6Ref}
         />
         <AnimatedBeam
            containerRef={containerRef}
            fromRef={div6Ref}
            toRef={div7Ref}
         />
      </div>
   )
}

